var html = `
<html>
	<head>
	<link rel="stylesheet" href = 'http://localhost/SECE2025_2/Client/customer/se_editor.css' type='text/css'>	
  <script                 src = 'http://localhost/SECE2025_2/Client/customer/panzoom.js'   type='text/javascript' ></script>
